import logo from './logo.png';
import './App.css';

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <button className="clicker" onClick="togglePause()">
          <img src={logo} className="App-logo" alt="logo" />
        </button>
      </header>
      <script>
        function togglePause() {
          this.nextElementSibling.classList.toggle('paused')
        }
      </script>
    </div>
  );
}

export default App;
